    @{id='sales-proposal'; title='Sales Proposal Template'; desc='Professional sales proposal'; content='<div class="section"><h2>Proposal Details</h2><div class="field"><label>Proposal Date:</label><input type="date"></div><div class="field"><label>Valid Until:</label><input type="date"></div></div><div class="section"><h2>Client Information</h2><div class="field"><label>Client Name:</label><input type="text"></div><div class="field"><label>Client Company:</label><input type="text"></div></div><div class="section"><h2>Proposed Solution</h2><textarea rows="6" placeholder="Describe the proposed solution or service"></textarea></div><div class="section"><h2>Pricing</h2><table><thead><tr><th>Item/Service</th><th>Quantity</th><th>Unit Price (₦)</th><th>Total (₦)</th></tr></thead><tbody><tr><td><input type="text" style="width:100%;border:none;"></td><td><input type="number" style="width:100%;border:none;"></td><td><input type="text" style="width:100%;border:none;"></td><td><input type="text" style="width:100%;border:none;"></td></tr></tbody></table></div><div class="section"><h2>Terms and Conditions</h2><textarea rows="4" placeholder="Payment terms and project timeline"></textarea></div>'}
