    @{id='refund-policy'; title='Refund/Return Policy Template'; desc='Return and refund policy'; content='<div class="section"><h2>Refund Policy</h2><div class="field"><label>Refund Eligibility Period:</label><input type="text" placeholder="e.g., 30 days from purchase"></div><div class="field"><label>Refund Conditions:</label><textarea rows="5" placeholder="Conditions under which refunds are granted"></textarea></div></div><div class="section"><h2>Return Process</h2><textarea rows="5" placeholder="Step-by-step return process"></textarea></div><div class="section"><h2>Non-Refundable Items</h2><textarea rows="4" placeholder="List items or services that are non-refundable"></textarea></div><div class="section"><h2>Contact Information</h2><div class="field"><label>Returns Email:</label><input type="email"></div><div class="field"><label>Returns Phone:</label><input type="tel"></div></div>'}
    @{id='inventory-checklist'; title='Inventory Checklist Template'; desc='Stock management checklist'; content='<div class="section"><h2>Inventory Details</h2><div class="field"><label>Inventory Date:</label><input type="date"></div><div class="field"><label>Location/Warehouse:</label><input type="text"></div></div><div class="section"><h2>Inventory Items</h2><table><thead><tr><th>Item Name</th><th>SKU/Code</th><th>Expected Qty</th><th>Actual Qty</th><th>Variance</th><th>Notes</th></tr></thead><tbody><tr><td><input type="text" style="width:100%;border:none;"></td><td><input type="text" style="width:100%;border:none;"></td><td><input type="number" style="width:100%;border:none;"></td><td><input type="number" style="width:100%;border:none;"></td><td><input type="number" style="width:100%;border:none;"></td><td><input type="text" style="width:100%;border:none;"></td></tr></tbody></table></div>'}
    @{id='vendor-agreement'; title='Vendor/Supplier Agreement Template'; desc='Supplier contract template'; content='<div class="section"><h2>Parties</h2><div class="field"><label>Buyer/Company:</label><input type="text"></div><div class="field"><label>Vendor/Supplier:</label><input type="text"></div></div><div class="section"><h2>Products/Services</h2><textarea rows="4" placeholder="Description of products or services to be supplied"></textarea></div><div class="section"><h2>Terms</h2><div class="field"><label>Payment Terms:</label><textarea rows="3" placeholder="Payment schedule and terms"></textarea></div><div class="field"><label>Delivery Terms:</label><textarea rows="3" placeholder="Delivery schedule and terms"></textarea></div></div><div class="section"><h2>Pricing</h2><div class="field"><label>Pricing Structure:</label><textarea rows="3" placeholder="Pricing details"></textarea></div></div>'}
    @{id='meeting-agenda'; title='Meeting Agenda Template'; desc='Structured meeting agenda'; content='<div class="section"><h2>Meeting Information</h2><div class="field"><label>Meeting Title:</label><input type="text"></div><div class="field"><label>Date:</label><input type="date"></div><div class="field"><label>Time:</label><input type="time"></div><div class="field"><label>Location:</label><input type="text"></div></div><div class="section"><h2>Attendees</h2><textarea rows="3" placeholder="List meeting attendees"></textarea></div><div class="section"><h2>Agenda Items</h2><table><thead><tr><th>Time</th><th>Topic</th><th>Presenter</th><th>Notes</th></tr></thead><tbody><tr><td><input type="text" style="width:100%;border:none;"></td><td><input type="text" style="width:100%;border:none;"></td><td><input type="text" style="width:100%;border:none;"></td><td><input type="text" style="width:100%;border:none;"></td></tr></tbody></table></div><div class="section"><h2>Action Items</h2><textarea rows="4" placeholder="Action items and follow-ups"></textarea></div>'}
    @{id='project-plan'; title='Project Plan Template'; desc='Project management framework'; content='<div class="section"><h2>Project Information</h2><div class="field"><label>Project Name:</label><input type="text"></div><div class="field"><label>Project Manager:</label><input type="text"></div><div class="field"><label>Start Date:</label><input type="date"></div><div class="field"><label>End Date:</label><input type="date"></div></div><div class="section"><h2>Project Objectives</h2><textarea rows="4" placeholder="Project goals and objectives"></textarea></div><div class="section"><h2>Project Scope</h2><textarea rows="4" placeholder="What is included and excluded from the project"></textarea></div><div class="section"><h2>Timeline and Milestones</h2><table><thead><tr><th>Milestone</th><th>Target Date</th><th>Status</th></tr></thead><tbody><tr><td><input type="text" style="width:100%;border:none;"></td><td><input type="date" style="width:100%;border:none;"></td><td><select style="width:100%;border:none;"><option>Not Started</option><option>In Progress</option><option>Completed</option></select></td></tr></tbody></table></div><div class="section"><h2>Budget</h2><div class="field"><label>Project Budget (₦):</label><input type="text"></div></div>'}
    @{id='social-media-calendar'; title='Social Media Content Calendar'; desc='Monthly social media scheduling'; content='<div class="section"><h2>Calendar Period</h2><div class="field"><label>Month/Year:</label><input type="text" placeholder="e.g., January 2026"></div></div><div class="section"><h2>Content Schedule</h2><table><thead><tr><th>Date</th><th>Platform</th><th>Content Type</th><th>Content Description</th><th>Status</th></tr></thead><tbody><tr><td><input type="date" style="width:100%;border:none;"></td><td><select style="width:100%;border:none;"><option>Facebook</option><option>Instagram</option><option>Twitter</option><option>LinkedIn</option></select></td><td><input type="text" style="width:100%;border:none;"></td><td><input type="text" style="width:100%;border:none;"></td><td><select style="width:100%;border:none;"><option>Scheduled</option><option>Published</option><option>Draft</option></select></td></tr></tbody></table></div>'}
    @{id='press-release'; title='Press Release Template'; desc='Professional press release'; content='<div class="section"><h2>Press Release Header</h2><div class="field"><label>FOR IMMEDIATE RELEASE</label></div><div class="field"><label>Headline:</label><input type="text" style="font-size:1.2em;font-weight:bold;"></div><div class="field"><label>Subheadline:</label><input type="text"></div></div><div class="section"><h2>Contact Information</h2><div class="field"><label>Contact Name:</label><input type="text"></div><div class="field"><label>Company:</label><input type="text"></div><div class="field"><label>Email:</label><input type="email"></div><div class="field"><label>Phone:</label><input type="tel"></div></div><div class="section"><h2>Press Release Body</h2><div class="field"><label>Location and Date:</label><input type="text" placeholder="e.g., Lagos, Nigeria - January 15, 2026"></div><div class="field"><label>Body:</label><textarea rows="10" placeholder="Write your press release content here"></textarea></div></div><div class="section"><h2>About</h2><textarea rows="4" placeholder="About section describing your company"></textarea></div>'}
    @{id='email-newsletter'; title='Email Newsletter Template'; desc='Customer newsletter template'; content='<div class="section"><h2>Newsletter Header</h2><div class="field"><label>Newsletter Title:</label><input type="text" style="font-size:1.3em;font-weight:bold;"></div><div class="field"><label>Issue Date:</label><input type="date"></div><div class="field"><label>Issue Number:</label><input type="text" placeholder="e.g., Issue #1"></div></div><div class="section"><h2>Featured Content</h2><div class="field"><label>Main Headline:</label><input type="text" style="font-size:1.1em;"></div><div class="field"><label>Featured Article:</label><textarea rows="6" placeholder="Main article content"></textarea></div></div><div class="section"><h2>Additional Sections</h2><div class="field"><label>Section 1 Title:</label><input type="text"></div><div class="field"><label>Section 1 Content:</label><textarea rows="4"></textarea></div><div class="field"><label>Section 2 Title:</label><input type="text"></div><div class="field"><label>Section 2 Content:</label><textarea rows="4"></textarea></div></div><div class="section"><h2>Call to Action</h2><div class="field"><label>CTA Text:</label><input type="text" placeholder="e.g., Visit our website, Contact us"></div></div>'}
)

foreach ($template in $templates) {
    $content = $baseTemplate -replace '{{TITLE}}', $template.title -replace '{{DESCRIPTION}}', $template.desc -replace '{{CONTENT}}', $template.content
    $filePath = Join-Path $templatesDir "$($template.id).html"
    Set-Content -Path $filePath -Value $content -Encoding UTF8
    Write-Host "Created: $($template.id).html"
}

Write-Host "All templates created successfully!"
